alignToNow = (tables=<-) => {
    _lastTime =
        (tables
            |> keep(columns: ["_time"])
            |> last(column: "_time")
            |> findRecord(fn: (key) => true, idx: 0))._time
    _offset = int(v: now()) - int(v: _lastTime)
    _offsetDuration = duration(v: _offset)

    return tables |> timeShift(duration: _offsetDuration)
}

